   */
  tags?: Record<string, string>;
}

/**
 * Output returned after DynamoDB table creation/update
 */
export interface Table extends Resource<"dynamo::Table">, TableProps {
  /**
   * ARN of the table
   * Format: arn:aws:dynamodb:region:account-id:table/table-name
