      const keySchema: KeySchemaElement[] = [
        {
          AttributeName: props.partitionKey.name,
          KeyType: "HASH",
        },
      ];

      if (props.sortKey) {
        attributeDefinitions.push({
          AttributeName: props.sortKey.name,
          AttributeType: props.sortKey.type,
