      }

      if (!tableActive) {
        throw new Error(
          `Timed out waiting for table ${props.tableName} to become active`
        );
      }

      return this({
        ...props,
        arn: tableDescription!.TableArn!,
        streamArn: tableDescription!.LatestStreamArn,
        tableId: tableDescription!.TableId!,
      });
