  environment?: string | undefined;
  sqlite?: boolean | undefined;
  namespaceId?: string | undefined;
}

/**
 * @example
 * // Create a basic Durable Object namespace for stateful chat rooms
 * const rooms = new DurableObjectNamespace("chat-rooms", {
